/*!
 * Lightweight self-hosted loader for @walletconnect/ethereum-provider UMD.
 * Keeps your <script src="/walletconnect/provider/index.umd.min.js"> stable,
 * while fetching the official bundle from CDNs (jsDelivr â†’ UNPKG).
 */
(function () {
  var cdns = [
    "https://cdn.jsdelivr.net/npm/@walletconnect/ethereum-provider@2.13.1/dist/index.umd.min.js",
    "https://unpkg.com/@walletconnect/ethereum-provider@2.13.1/dist/index.umd.min.js"
  ];
  var loaded = false, lastErr = null;
  function inject(src) {
    var s = document.createElement("script");
    s.src = src; s.async = true; s.crossOrigin = "anonymous";
    s.onload = function(){ loaded = true; };
    s.onerror = function(e){ lastErr = e || new Error("load failed: " + src); tryNext(); };
    document.head.appendChild(s);
  }
  function tryNext() {
    if (loaded) return;
    var next = cdns.shift();
    if (!next) {
      console.error("[WC Loader] Failed to load WalletConnect Ethereum Provider UMD.", lastErr);
      var ev = new CustomEvent("walletconnect:provider:error", { detail: { error: String(lastErr) }});
      window.dispatchEvent(ev);
      return;
    }
    inject(next);
  }
  tryNext();
})();